package cc.weiui.pay.library.alipay;import java.util.Map;import android.text.TextUtils;public class PayResult {    private String resultStatus;    private String result;    private String memo;    private String msgName;    public PayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            } else if (TextUtils.equals(key, "msgName")) {                msgName = rawResult.get(key);            }        }    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo + "};result={" + result + "};msgName={" + msgName + "}";    }    /**     * @return the resultStatus     */    public String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        return memo;    }    /**     * @return the msgName     */    public String getMsgName() {        return msgName;    }    /**     * @return the result     */    public String getResult() {        return result;    }}